mapper.insert:
insert into webapp_user (
PASSWORD,
PHONE
) values (
#{password},
#{phone}
)

mapper.update:
update webapp_user
set
PASSWORD = #{password}
where
phone= #{phone}

mapper.select:
select u.phone, u.id ,u.type ,u.lon, u.lat from webapp_user u
where
u.lat between #{minLat} and #{maxLat}
and
u.lon between #{minLng} and
#{maxLng}
